import React, { createContext, useEffect, useState } from "react";
import axios from "axios";
import createAuthRefreshInterceptor from "axios-auth-refresh";
axios.defaults.withCredentials = true;
const AuthContext = createContext();

const AuthProvider = ({ children }) => {
  const [currentUser, setCurrentUser] = useState();
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(true);
  const [token, setToken] = useState(null);

  const refreshAuthLogic = (failedRequest) => {
    return axios
      .post("http://localhost:5000/api/refresh_token")
      .then((tokenRefreshResponse) => {
        setToken(tokenRefreshResponse.data.token);
        failedRequest.response.config.headers["x-access-token"] =
          tokenRefreshResponse.data.token;
        return Promise.resolve();
      })
      .catch((err) => {
        console.log(err);
      });
  };

  createAuthRefreshInterceptor(axios, refreshAuthLogic);

  const getUser = () => {
    axios
      .get("http://localhost:5000/api/userInfo", {
        headers: { "x-access-token": token },
      })
      .then((response) => {
        if (response.data.auth) {
          setCurrentUser(response.data.user);
        } else {
          if (response.data.message === "Authentication failed") {
            setToken();
          }
        }
        setLoading(false);
      })
      .catch((e) => {
        console.log(e);
        setToken();
        window.location.reload();
      });
  };

  const login = (username, password) => {
    axios
      .post("http://localhost:5000/api/login", {
        username: username,
        password: password,
      })
      .then((res) => {
        console.log(res.data);
        if (res.data.auth) {
          setCurrentUser(res.data.user);
          setToken(res.data.token);
          setMessage("");
        } else {
          setMessage(res.data.message);
        }
      });
  };

  const logout = () => {
    setToken();
    window.location.reload();
  };

  useEffect(() => {
    getUser();
  }, []);

  const AuthContextValue = {
    currentUser,
    message,
    login,
    logout,
    token,
  };
  return (
    <AuthContext.Provider value={AuthContextValue}>
      {!loading && children}
    </AuthContext.Provider>
  );
};

const useAuth = () => React.useContext(AuthContext);

export { AuthProvider, useAuth };
